mapper.select:
select * from doctor  where status=0

   and  name = #{name}

order by name

mapper.update:
        update doctor set name=#{doctor.name}, gender=#{doctor.gender},hospital_id=#{doctor.hospitalId}  ,hospital_name=#{doctor.hospitalName} ,level=#{doctor.level}, status=#{doctor.status},
		 specialty=#{doctor.specialty}, introduction=#{doctor.introduction}
		, treatment_effect=#{doctor.treatmentEffect}, service_attitude=#{doctor.serviceAttitude}, 
		head_img_url=#{doctor.headImgUrl},qr_img_url=#{doctor.qrImgUrl},dept_id=#{doctor.deptId}  ,dept_name=#{doctor.deptName} , user_id=#{doctor.userId},qualification_id=#{doctor.qualificationId},grade=#{doctor.grade} where id=#{doctor.id}
